    <h1>Catégorie 1</h1>
    \t<article>
      \t\t<h2>{{ article.title }}</h2>
      \t<div class=\"metadata\">Ecrit le {{ article.createdAt | date('d/m/Y') }} à {{ article.createdAt | date('H:i') }} dans la catégorie {{ article.category.title }}</div>
      \t\t<div class=\"content\">
        \t<img src=\"{{ article.image }}\" alt=\"#\">
        \t{{ article.content | raw }}
        \t<a href=\"{{ path('blog_show', {'id': article.id}) }}\" class=\"btn-primary\">Lire la suite</a>
        </div>
    </article>
  {% endif %}

{% endfor %}

</section>


{% endblock %}", "blog/category1.html.twig", "/var/www/html/sf_one/templates/blog/category1.html.twig");
